import React, { useState } from 'react';
import { Button, Input, FormControl, Label, HelperMessage, useLoading } from '@yamada-ui/react';

function Signup() {
  const { screen } = useLoading();
  const [isSubmitting, setSubmitting] = useState(false);

  const handleSignup = async (event) => {
    event.preventDefault();
    setSubmitting(true);
    screen.start();

    try {
      await new Promise(resolve => setTimeout(resolve, 2000));
    } finally {
      setSubmitting(false);
      screen.finish();
    }
  };

  return (
    <div className="slideshow bg-gradient-to-b from-transparent to-black via-transparent opacity-75 fixed inset-0 z-0">
      <div className="max-w-md w-full mx-auto p-4 mt-20 bg-white bg-opacity-50 rounded-lg shadow-lg">
        <form onSubmit={handleSignup}>
          <FormControl label="ユーザー名">
            <Label>ユーザー名</Label>
            <Input type='text' placeholder='ユーザー名' />
            <HelperMessage>使用するユーザー名を入力してください。</HelperMessage>
          </FormControl>
          <FormControl label="パスワード">
            <Label>パスワード</Label>
            <Input type='password' placeholder='パスワード' />
            <HelperMessage>パスワードは6文字以上です。</HelperMessage>
          </FormControl>
          <FormControl label="パスワード確認">
            <Label>パスワード確認</Label>
            <Input type='password' placeholder='パスワード確認' />
            <HelperMessage>パスワードを再入力してください。</HelperMessage>
          </FormControl>
          <Button type='submit' isLoading={isSubmitting} loadingText="登録中...">登録</Button>
        </form>
      </div>
    </div>
  );
}

export default Signup;
