import type { ComponentStyle } from "@yamada-ui/core"
import {
  getMemoizedObject as get,
  isAccessible,
  isGray,
  shadeColor,
  transparentizeColor,
} from "@yamada-ui/utils"

export const Toggle: ComponentStyle = {
  baseStyle: {
    cursor: "pointer",
    rounded: "md",
    transitionProperty: "common",
    transitionDuration: "slower",
    _disabled: {
      opacity: 0.4,
      cursor: "not-allowed",
      boxShadow: "none",
    },
    _readOnly: {
      cursor: "default",
      _ripple: {
        display: "none",
      },
    },
  },

  variants: {
    subtle: ({ theme: t, colorMode: m, colorScheme: c = "gray" }) => {
      return {
        _hover: {
          bg: [
            isGray(c) ? `blackAlpha.200` : `${c}.50`,
            transparentizeColor(`${c}.500`, 0.12)(t, m),
          ],
        },
        _selected: {
          bg: [
            isGray(c) ? `${c}.50` : `${c}.100`,
            shadeColor(`${c}.300`, 72)(t, m),
          ],
        },
        _focusVisible: {
          borderColor: "transparent",
          boxShadow: "outline",
        },
      }
    },
    solid: ({ theme: t, colorMode: m, colorScheme: c = "gray" }) => {
      return {
        _hover: {
          bg: [
            isGray(c) ? `blackAlpha.200` : `${c}.100`,
            shadeColor(`${c}.300`, 72)(t, m),
          ],
        },
        _selected: {
          bg: isGray(c)
            ? [`${c}.100`, `${c}.700`]
            : [isAccessible(c) ? `${c}.400` : `${c}.500`, `${c}.600`],
          color: [isGray(c) || isAccessible(c) ? `black` : `white`, `white`],
        },
        _focusVisible: {
          borderColor: "transparent",
          boxShadow: "outline",
        },
      }
    },
    outline: ({ theme: t, colorMode: m, colorScheme: c = "gray" }) => {
      return {
        border: "1px solid",
        borderColor: "border",
        _hover: {
          bg: [
            isGray(c) ? `blackAlpha.200` : `${c}.50`,
            transparentizeColor(`${c}.500`, 0.12)(t, m),
          ],
        },
        _selected: {
          bg: [
            isGray(c) ? `${c}.50` : `${c}.100`,
            shadeColor(`${c}.300`, 72)(t, m),
          ],
          borderColor: [
            isGray(c) ? `${c}.50` : `${c}.100`,
            shadeColor(`${c}.300`, 72)(t, m),
          ],
        },
        _focusVisible: {
          boxShadow: "outline",
          _invalid: {
            borderColor: "transparent",
          },
        },
      }
    },
    unstyled: {
      bg: "none",
      color: "inherit",
      display: "inline",
      lineHeight: "inherit",
      m: 0,
      p: 0,
      _ripple: {
        display: "none",
      },
    },
  },

  sizes: {
    xs: ({ theme: t }) => ({
      minBoxSize: 6,
      fontSize: "xs",
      lineHeight: get(t, "sizes.6"),
    }),
    sm: ({ theme: t }) => ({
      minBoxSize: 8,
      fontSize: "sm",
      lineHeight: get(t, "sizes.8"),
    }),
    md: ({ theme: t }) => ({
      minBoxSize: 10,
      fontSize: "md",
      lineHeight: get(t, "sizes.10"),
    }),
    lg: ({ theme: t }) => ({
      minBoxSize: 12,
      fontSize: "lg",
      lineHeight: get(t, "sizes.12"),
    }),
  },

  defaultProps: {
    variant: "subtle",
    size: "md",
    colorScheme: "gray",
  },
}
