import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {
    Box,
    Button,
    Input,
    FormControl,
    Label,
    HelperMessage,
    useLoading,
    Modal,
    ModalOverlay,
    ModalCloseButton,
    ModalHeader,
    ModalBody,
    ModalFooter,
    useDisclosure
} from '@yamada-ui/react';
import AnimatePage from './AnimatePage';
import ImageFade from './ImageFade';  // 確認してください。このコンポーネントが正しくインポートされているか

function Signin() {
    const { screen } = useLoading();
    const [isSubmitting, setSubmitting] = useState(false);
    const [userID, setUserID] = useState('');
    const [password, setPassword] = useState('');
    const { isOpen, onOpen, onClose } = useDisclosure();
    const [currentImage, setCurrentImage] = useState(0);
    const images = [
        "https://dragon-ball-official.com/assets/img/intro/intro_1.png",
        "https://dragon-ball-official.com/assets/img/intro/intro_2.png",
        "https://dragon-ball-official.com/assets/img/intro/intro_3.png"
    ];

    useEffect(() => {
        const intervalId = setInterval(() => {
            setCurrentImage((currentImage + 1) % images.length);
        }, 8000); // 8秒ごとに画像を切り替える

        return () => clearInterval(intervalId); // コンポーネントのアンマウント時にインターバルをクリア
    }, [currentImage, images.length]); // images.length を依存関係配列に追加


    const handleLogin = async (event) => {
        event.preventDefault();
        if (!userID || !password || password.length < 6) {
            onOpen();
            return;
        }
        setSubmitting(true);
        screen.start();

        try {
            await new Promise(resolve => setTimeout(resolve, 2000)); // Sample delay simulation
        } finally {
            setSubmitting(false);
            screen.finish();
        }
    };

    return (
        <AnimatePage direction="right">
            <div className="container">
                <div className="left-panel">
                    {/* 画像を表示する部分 */}
                    <ImageFade src={images[currentImage]} key={currentImage} />
                </div>
                <div className="right-panel">
                    <h1>ログイン</h1>
                    <form onSubmit={handleLogin}>
                        <FormControl isRequired label="ユーザーID" errorMessage={!userID && "ユーザーIDは必須です。"}>
                            <Label>ユーザーID</Label>
                            <Input type='text' placeholder='ユーザーID' value={userID} onChange={(e) => setUserID(e.target.value)} />
                            <HelperMessage>IDを入力してください。</HelperMessage>
                        </FormControl>
                        <FormControl isRequired label="パスワード" errorMessage={!password ? "パスワードは必須です。" : (password.length < 6 && "パスワードは6文字以上必要です。")}>
                            <Label>パスワード</Label>
                            <Input type='password' placeholder='パスワード' value={password} onChange={(e) => setPassword(e.target.value)} />
                        </FormControl>
                        <Button type='submit' isLoading={isSubmitting} loadingText="ログイン中...">ログイン</Button>
                        <div className="additional-links">
                            <Link to="/resetpass">パスワードをお忘れの方</Link>
                            <Link to="/signup"><Button>新規登録</Button></Link>
                        </div>
                    </form>
                </div>
            </div>
        </AnimatePage>
    );


}

export default Signin;
