// AnimatePage.js
import { motion } from 'framer-motion';

const pageVariants = (direction = 'left') => ({
    initial: {
        opacity: 0,
        x: direction === 'left' ? '100vw' : '-100vw',
    },
    in: {
        opacity: 0,
        x: 0
    },
    out: {
        opacity: 0,
        x: direction === 'left' ? '-100vw' : '100vw',
    }
});

const pageTransition = {
    type: "tween",
    ease: "anticipate",
    duration: 0.5
};

const AnimatePage = ({ children, direction }) => (
    <motion.div
        initial="initial"
        animate="in"
        exit="out"
        variants={pageVariants(direction)}
        transition={pageTransition}
    >
        {children}
    </motion.div>
);

export default AnimatePage;
