import React from 'react';
import { motion } from 'framer-motion';

const imageVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1 }
};

const transition = {
    duration: 2
};

const ImageFade = ({ src, key }) => (
    <motion.img
        key={key}
        src={src}
        initial="hidden"
        animate="visible"
        exit="hidden"
        variants={imageVariants}
        transition={transition}
        style={{ width: '100%', height: '100%' }}  // 画像のサイズ調整が必要であればここを変更
    />
);

export default ImageFade;
