import type { CSSUIObject, HTMLUIProps, ThemeProps } from "@yamada-ui/core"
import {
  ui,
  forwardRef,
  useMultiComponentStyle,
  omitThemeProps,
} from "@yamada-ui/core"
import { useControllableState } from "@yamada-ui/use-controllable-state"
import { createDescendant } from "@yamada-ui/use-descendant"
import type { LazyMode } from "@yamada-ui/use-disclosure"
import {
  createContext,
  cx,
  findChildren,
  pickChildren,
  getValidChildren,
} from "@yamada-ui/utils"
import type { Dispatch, SetStateAction } from "react"
import { useEffect, useState } from "react"
import type { TabListProps, TabPanelsProps } from "./"
import { TabList, TabPanels, Tab, TabPanel } from "./"

const {
  DescendantsContextProvider,
  useDescendantsContext: useTabsDescendantsContext,
  useDescendants,
  useDescendant: useTabsDescendant,
} = createDescendant<HTMLButtonElement>()

export { useTabsDescendantsContext, useTabsDescendant }

type TabsContext = Omit<TabsOptions, "index" | "defaultIndex" | "onChange"> & {
  focusedIndex: number
  setFocusedIndex: Dispatch<SetStateAction<number>>
  selectedIndex: number
  setSelectedIndex: Dispatch<SetStateAction<number>>
  styles: Record<string, CSSUIObject>
  disableRipple: boolean
}

const [TabsProvider, useTabsContext] = createContext<TabsContext>({
  name: "TabsContext",
  errorMessage: `useTabsContext returned is 'undefined'. Seems you forgot to wrap the components in "<Tabs />"`,
})

export { useTabsContext }

type TabsOptions = {
  /**
   * The index of the selected tab.
   */
  index?: number
  /**
   * The index of the selected tab.
   */
  defaultIndex?: number
  /**
   * If `true`, tabs will stretch to width of the tablist.
   *
   * @default false
   */
  isFitted?: boolean
  /**
   * The alignment of the tabs.
   */
  align?: "start" | "end" | "center"
  /**
   * If `true`, the tabs will be manually activated and　display its panel by pressing Space or Enter.
   *
   * If `false`, the tabs will be automatically activated and their panel is displayed when they receive focus.
   *
   * @default false
   */
  isManual?: boolean
  /**
   * If `true`, rendering of the tab panel's will be deferred until it is selected.
   *
   * @default true
   */
  isLazy?: boolean
  /**
   * The lazy behavior of tab panels' content when not active. Only works when `isLazy={true}`.
   *
   * - `unmount`: The content of inactive tab panels are always unmounted.
   * - `keepMounted`: The content of inactive tab panels is initially unmounted, but stays mounted when selected.
   *
   * @default 'unmount'
   */
  lazyBehavior?: LazyMode
  /**
   * The orientation of the tab list.
   *
   * @default 'horizontal'
   */
  orientation?: "vertical" | "horizontal"
  /**
   * The callback invoked when the index changes.
   */
  onChange?: (index: number) => void
  /**
   * Props for tab list component.
   */
  tabListProps?: TabListProps
  /**
   * Props for tab panels components.
   */
  tabPanelsProps?: TabPanelsProps
  /**
   * If `true`, disable ripple effects when pressing the tab.
   *
   * @default false
   */
  disableRipple?: boolean
}

export type TabsProps = Omit<HTMLUIProps<"div">, "onChange"> &
  ThemeProps<"Tabs"> &
  TabsOptions

/**
 * `Tabs` is a component for switching between different display areas.
 *
 * @see Docs https://yamada-ui.com/components/disclosure/tabs
 */
export const Tabs = forwardRef<TabsProps, "div">(
  ({ align = "start", ...props }, ref) => {
    const [styles, mergedProps] = useMultiComponentStyle("Tabs", {
      align,
      ...props,
    })
    const {
      className,
      index,
      defaultIndex = 0,
      onChange,
      isFitted,
      isManual,
      isLazy = true,
      disableRipple = false,
      lazyBehavior = "keepMounted",
      orientation = "horizontal",
      tabListProps,
      tabPanelsProps,
      children,
      ...rest
    } = omitThemeProps(mergedProps)

    const [focusedIndex, setFocusedIndex] = useState<number>(defaultIndex)

    const [selectedIndex, setSelectedIndex] = useControllableState({
      value: index,
      defaultValue: defaultIndex,
      onChange,
    })

    const descendants = useDescendants()

    const validChildren = getValidChildren(children)

    const [customTabList] = findChildren(validChildren, TabList)
    const [customTabPanels] = findChildren(validChildren, TabPanels)
    const cloneTabs = pickChildren(validChildren, Tab)
    const cloneTabPanels = pickChildren(validChildren, TabPanel)

    useEffect(() => {
      if (index != null) setFocusedIndex(index)
    }, [index])

    const css: CSSUIObject = { w: "100%", ...styles.container }

    return (
      <DescendantsContextProvider value={descendants}>
        <TabsProvider
          value={{
            focusedIndex,
            setFocusedIndex,
            selectedIndex,
            setSelectedIndex,
            disableRipple,
            isFitted,
            align,
            isManual,
            isLazy,
            lazyBehavior,
            orientation,
            tabListProps,
            tabPanelsProps,
            styles,
          }}
        >
          <ui.div
            ref={ref}
            className={cx("ui-tabs", className)}
            __css={css}
            {...rest}
          >
            {customTabList ?? <TabList>{cloneTabs}</TabList>}
            {customTabPanels ?? <TabPanels>{cloneTabPanels}</TabPanels>}
          </ui.div>
        </TabsProvider>
      </DescendantsContextProvider>
    )
  },
)
import type { CSSUIObject, HTMLUIProps } from "@yamada-ui/core"
import { ui, forwardRef } from "@yamada-ui/core"
import { cx, handlerAll } from "@yamada-ui/utils"
import type { KeyboardEvent, KeyboardEventHandler } from "react"
import { useCallback } from "react"
import { useTabsContext, useTabsDescendantsContext } from "./tabs"

export type TabListProps = HTMLUIProps<"div">

export const TabList = forwardRef<TabListProps, "div">(
  ({ className, ...rest }, ref) => {
    const { focusedIndex, orientation, tabListProps, styles } = useTabsContext()

    const descendants = useTabsDescendantsContext()

    const isVertical = orientation === "vertical"

    const onNext = useCallback(() => {
      const next = descendants.enabledNextValue(focusedIndex)

      if (next) next.node?.focus()
    }, [descendants, focusedIndex])

    const onPrev = useCallback(() => {
      const prev = descendants.enabledPrevValue(focusedIndex)

      if (prev) prev.node?.focus()
    }, [descendants, focusedIndex])

    const onFirst = useCallback(() => {
      const first = descendants.enabledFirstValue()

      if (first) first.node?.focus()
    }, [descendants])

    const onLast = useCallback(() => {
      const last = descendants.enabledLastValue()

      if (last) last.node?.focus()
    }, [descendants])

    const onKeyDown = useCallback(
      (ev: KeyboardEvent) => {
        const actions: Record<string, KeyboardEventHandler> = {
          ArrowLeft: () => (!isVertical ? onPrev() : {}),
          ArrowRight: () => (!isVertical ? onNext() : {}),
          ArrowDown: () => (isVertical ? onNext() : {}),
          ArrowUp: () => (isVertical ? onPrev() : {}),
          Home: onFirst,
          End: onLast,
        }

        const action = actions[ev.key]

        if (!action) return

        ev.preventDefault()
        action(ev)
      },
      [onFirst, onLast, isVertical, onPrev, onNext],
    )

    const css: CSSUIObject = { display: "flex", ...styles.tabList }

    return (
      <ui.div
        ref={ref}
        className={cx("ui-tabs__list", className)}
        role="tablist"
        __css={css}
        {...tabListProps}
        {...rest}
        onKeyDown={handlerAll(rest.onKeyDown, onKeyDown)}
      />
    )
  },
)
import type { CSSUIObject, HTMLUIProps } from "@yamada-ui/core"
import { ui, forwardRef } from "@yamada-ui/core"
import { Ripple, useRipple } from "@yamada-ui/ripple"
import type { UseClickableProps } from "@yamada-ui/use-clickable"
import { useClickable } from "@yamada-ui/use-clickable"
import { ariaAttr, cx, handlerAll, mergeRefs } from "@yamada-ui/utils"
import { useTabsContext, useTabsDescendant } from "./tabs"

export type TabProps = HTMLUIProps<"button"> &
  Omit<UseClickableProps, "ref" | "color">

export const Tab = forwardRef<TabProps, "button">(
  (
    {
      className,
      isDisabled,
      isFocusable,
      clickOnEnter,
      clickOnSpace,
      children,
      ...props
    },
    ref,
  ) => {
    const {
      selectedIndex,
      isManual,
      disableRipple,
      setSelectedIndex,
      setFocusedIndex,
      styles,
    } = useTabsContext()

    const { index, register } = useTabsDescendant({
      disabled: isDisabled && !isFocusable,
    })

    const isSelected = index === selectedIndex

    const onFocus = () => {
      setFocusedIndex(index)

      if (!isManual && !(isDisabled && isFocusable)) setSelectedIndex(index)
    }

    const rest = useClickable({
      ref: mergeRefs(register, ref),
      isDisabled,
      isFocusable,
      clickOnEnter,
      clickOnSpace,
      onClick: handlerAll(props.onClick, () => setSelectedIndex(index)),
    })
    const { onPointerDown, ...rippleProps } = useRipple({
      ...rest,
      isDisabled: disableRipple || isDisabled,
    })

    const css: CSSUIObject = {
      position: "relative",
      overflow: "hidden",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      outline: "0",
      ...styles.tab,
    }

    return (
      <ui.button
        className={cx("ui-tabs__tab", className)}
        __css={css}
        role="tab"
        {...props}
        {...rest}
        type="button"
        tabIndex={isSelected ? 0 : -1}
        aria-selected={ariaAttr(isSelected)}
        onPointerDown={onPointerDown}
        onFocus={isDisabled ? undefined : handlerAll(props.onFocus, onFocus)}
      >
        {children}

        <Ripple isDisabled={disableRipple || isDisabled} {...rippleProps} />
      </ui.button>
    )
  },
)
import type { CSSUIObject, HTMLUIProps } from "@yamada-ui/core"
import { ui, forwardRef } from "@yamada-ui/core"
import { createContext, cx, getValidChildren } from "@yamada-ui/utils"
import { createElement } from "react"
import { useTabsContext } from "./tabs"

const [TabPanelProvider, useTabPanelContext] = createContext<{
  isSelected: boolean
  selectedIndex: number
}>({})

export { useTabPanelContext }

export type TabPanelsProps = HTMLUIProps<"div">

export const TabPanels = forwardRef<TabPanelsProps, "div">(
  ({ className, children, ...rest }, ref) => {
    const { selectedIndex, tabPanelsProps, styles } = useTabsContext()

    const validChildren = getValidChildren(children)

    const cloneChildren = validChildren.map((child, index) => {
      const isSelected = index === selectedIndex

      return createElement(
        TabPanelProvider,
        { key: index, value: { isSelected, selectedIndex } },
        child,
      )
    })

    const css: CSSUIObject = {
      w: "100%",
      ...styles.tabPanels,
    }

    return (
      <ui.div
        ref={ref}
        className={cx("ui-tabs__panels", className)}
        __css={css}
        {...tabPanelsProps}
        {...rest}
      >
        {cloneChildren}
      </ui.div>
    )
  },
)
import type { CSSUIObject, HTMLUIProps } from "@yamada-ui/core"
import { ui, forwardRef } from "@yamada-ui/core"
import { useLazyDisclosure } from "@yamada-ui/use-disclosure"
import { cx } from "@yamada-ui/utils"
import { useRef } from "react"
import { useTabPanelContext } from "./tab-panels"
import { useTabsContext } from "./tabs"

export type TabPanelProps = HTMLUIProps<"div">

export const TabPanel = forwardRef<TabPanelProps, "div">(
  ({ className, children, ...rest }, ref) => {
    const { isLazy: enabled, lazyBehavior: mode, styles } = useTabsContext()
    const { isSelected } = useTabPanelContext()

    const hasBeenSelected = useRef<boolean>(false)

    if (isSelected) hasBeenSelected.current = true

    const shouldRenderChildren = useLazyDisclosure({
      wasSelected: hasBeenSelected.current,
      isSelected,
      enabled,
      mode,
    })

    const css: CSSUIObject = { ...styles.tabPanel }

    return (
      <ui.div
        ref={ref}
        className={cx("ui-tabs__panel", className)}
        role="tabpanel"
        __css={css}
        {...rest}
        hidden={!isSelected}
      >
        {shouldRenderChildren ? children : null}
      </ui.div>
    )
  },
)
