import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Button, Input, FormControl, Label, HelperMessage, useLoading } from '@yamada-ui/react';

function Signin() {
    const { screen } = useLoading();
    const [isSubmitting, setSubmitting] = useState(false);

    const handleLogin = async (event) => {
        event.preventDefault();
        setSubmitting(true);
        screen.start();

        try {
            await new Promise(resolve => setTimeout(resolve, 2000)); // サンプルの遅延シミュレーション
            // 実際のログインロジックをここに追加
        } finally {
            setSubmitting(false);
            screen.finish();
        }
    };

    return (
        <div>
            <form onSubmit={handleLogin}>
                <FormControl label="ユーザーID">
                    <Label>ユーザーID</Label>
                    <Input type='text' placeholder='ユーザーID' />
                    <HelperMessage>IDを入力してください。</HelperMessage>
                </FormControl>
                <FormControl label="パスワード">
                    <Label>パスワード</Label>
                    <Input type='password' placeholder='パスワード' />
                </FormControl>
                <Button type='submit' isLoading={isSubmitting} loadingText="ログイン中...">ログイン</Button>
            </form>

            <Link to="/resetpass">パスワードをお忘れの方</Link>
            <Link to="/signup"><Button>新規登録</Button></Link>
        </div>
    );
}

export default Signin;
