import type {
  HTMLUIProps,
  ThemeProps,
  ComponentArgs,
  UIPropGetter,
} from "@yamada-ui/core"
import { ui, useMultiComponentStyle, omitThemeProps } from "@yamada-ui/core"
import type { FormControlOptions } from "@yamada-ui/form-control"
import {
  useFormControl,
  useFormControlProps,
  formControlProperties,
} from "@yamada-ui/form-control"
import type { SVGMotionProps } from "@yamada-ui/motion"
import { AnimatePresence, motion } from "@yamada-ui/motion"
import { trackFocusVisible } from "@yamada-ui/use-focus-visible"
import type { Dict, Merge, PropGetter } from "@yamada-ui/utils"
import {
  cx,
  useCallbackRef,
  useSafeLayoutEffect,
  useUpdateEffect,
  handlerAll,
  dataAttr,
  mergeRefs,
  funcAll,
  splitObject,
} from "@yamada-ui/utils"
import type {
  ChangeEvent,
  ChangeEventHandler,
  CSSProperties,
  FC,
  FocusEventHandler,
  InputHTMLAttributes,
  KeyboardEvent,
  ReactElement,
  SyntheticEvent,
  ForwardedRef,
  Ref,
  DOMAttributes,
} from "react"
import {
  cloneElement,
  useCallback,
  useEffect,
  useRef,
  useState,
  forwardRef,
  useId,
} from "react"
import { useCheckboxGroupContext } from "./checkbox-group"

export type UseCheckboxProps<Y extends string | number = string> =
  FormControlOptions & {
    /**
     * id assigned to input.
     */
    id?: string
    /**
     * The HTML `name` attribute used for forms.
     */
    name?: string
    /**
     * The value to be used in the checkbox input.
     */
    value?: Y
    /**
     * If `true`, the checkbox will be initially checked.
     *
     * @default false
     */
    defaultIsChecked?: boolean
    /**
     * If `true`, the checkbox will be checked.
     *
     * @default false
     */
    isChecked?: boolean
    /**
     * If `true`, the checkbox will be indeterminate.
     *
     * @default false
     */
    isIndeterminate?: boolean
    /**
     * The callback invoked when the checked state changes.
     */
    onChange?: ChangeEventHandler<HTMLInputElement>
    /**
     * The callback invoked when the checkbox is focused.
     */
    onFocus?: FocusEventHandler<HTMLInputElement>
    /**
     * The callback invoked when the checkbox is blurred.
     */
    onBlur?: FocusEventHandler<HTMLInputElement>
    /**
     * The tab-index property of the underlying input element.
     */
    tabIndex?: number
  }

export const useCheckbox = <
  Y extends string | number = string,
  M extends Dict = Dict,
>({
  id,
  ...props
}: UseCheckboxProps<Y> & M) => {
  id ??= useId()
  const {
    id: _id,
    name,
    value,
    isChecked: isCheckedProp,
    defaultIsChecked,
    tabIndex,
    required,
    disabled,
    readOnly,
    isIndeterminate,
    onChange: onChangeProp,
    onFocus: onFocusProp,
    onBlur: onBlurProp,
    ...computedProps
  } = useFormControlProps({ id, ...props })
  const [{ "aria-readonly": _ariaReadonly, ...formControlProps }, rest] =
    splitObject(computedProps, formControlProperties)

  const [isFocusVisible, setIsFocusVisible] = useState<boolean>(false)
  const [isFocused, setFocused] = useState<boolean>(false)
  const [isHovered, setHovered] = useState<boolean>(false)
  const [isActive, setActive] = useState<boolean>(false)

  const inputRef = useRef<HTMLInputElement>(null)
  const [isLabel, setIsLabel] = useState<boolean>(true)

  const [isChecked, setIsChecked] = useState<boolean>(!!defaultIsChecked)

  const isControlled = isCheckedProp !== undefined
  const checked = isControlled ? (isCheckedProp as boolean) : isChecked

  const onChange = useCallbackRef(
    (ev: ChangeEvent<HTMLInputElement>) => {
      if (readOnly || disabled) {
        ev.preventDefault()

        return
      }

      if (!isControlled)
        setIsChecked(!checked || isIndeterminate ? true : ev.target.checked)

      onChangeProp?.(ev)
    },
    [readOnly, disabled, isControlled, checked, isIndeterminate],
  )
  const onFocus = useCallbackRef(onFocusProp)
  const onBlur = useCallbackRef(onBlurProp)

  const onKeyDown = useCallback(
    ({ key }: KeyboardEvent<Element>) => {
      if (key === " ") setActive(true)
    },
    [setActive],
  )

  const onKeyUp = useCallback(
    ({ key }: KeyboardEvent<Element>) => {
      if (key === " ") setActive(false)
    },
    [setActive],
  )

  useEffect(() => {
    return trackFocusVisible(setIsFocusVisible)
  }, [])

  useSafeLayoutEffect(() => {
    if (inputRef.current)
      inputRef.current.indeterminate = Boolean(isIndeterminate)
  }, [isIndeterminate])

  useUpdateEffect(() => {
    if (disabled) setFocused(false)
  }, [disabled, setFocused])

  useSafeLayoutEffect(() => {
    if (!inputRef.current?.form) return

    inputRef.current.form.onreset = () => setIsChecked(!!defaultIsChecked)
  }, [])

  useSafeLayoutEffect(() => {
    if (!inputRef.current) return

    if (inputRef.current.checked !== checked)
      setIsChecked(inputRef.current.checked)
  }, [inputRef.current])

  const getContainerProps: UIPropGetter = useCallback(
    (props = {}, ref = null) => ({
      ...formControlProps,
      ...props,
      ref: mergeRefs(ref, (el: HTMLElement | undefined) => {
        if (el) setIsLabel(el.tagName === "LABEL")
      }),
      "data-checked": dataAttr(checked),
      onClick: handlerAll(props.onClick, () => {
        if (isLabel) return

