import React from 'react';
import { Link } from 'react-router-dom';
import { Button, Input } from '@yamada-ui/react';
import { useLoading } from '@yamada-ui/react';

function Signin() {
    const { screen, page, background } = useLoading();

    const handleLogin = async (event) => {
        event.preventDefault();
        screen.start();

        try {
            await new Promise(resolve => setTimeout(resolve, 2000)); // サンプルの遅延シミュレーション
            // 実際のログインロジックをここに追加
        } finally {
            screen.finish();
        }
    };

    return (
        <div>
            <form onSubmit={handleLogin}>
                <Input type='text' placeholder='ユーザーID' />
                <Input type='password' placeholder='パスワード' />
                <Button type='submit'>ログイン</Button>
            </form>

            <Link to="/resetpass">パスワードをお忘れの方</Link>
            <Link to="/signup"><Button>新規登録</Button></Link>
        </div>
    );
}

export default Signin;
