import type { CSSUIObject, HTMLUIProps } from "@yamada-ui/core"
import { ui, forwardRef } from "@yamada-ui/core"
import type { IconProps } from "@yamada-ui/icon"
import { ChevronIcon } from "@yamada-ui/icon"
import { cx } from "@yamada-ui/utils"
import type { FC, PropsWithChildren, ReactNode } from "react"
import { useAccordionContext } from "./accordion"
import { useAccordionItemContext } from "./accordion-item"

type AccordionLabelOptions = {
  /**
   * The accordion icon to use.
   */
  icon?:
    | ReactNode
    | ((props: { isExpanded: boolean; isDisabled: boolean }) => ReactNode)
}

export type AccordionLabelProps = HTMLUIProps<"button"> & AccordionLabelOptions

export const AccordionLabel = forwardRef<AccordionLabelProps, "button">(
  ({ className, icon: customIcon, children, ...rest }, ref) => {
    const {
      isOpen,
      isDisabled = false,
      icon: supplementIcon,
      getLabelProps,
    } = useAccordionItemContext()
    const { icon: generalIcon, iconHidden, styles } = useAccordionContext()

    const css: CSSUIObject = {
      display: "inline-flex",
      alignItems: "center",
      width: "100%",
      outline: 0,
      ...styles.button,
    }

    const cloneCustomIcon =
      typeof customIcon === "function"
        ? customIcon({
            isExpanded: isOpen,
            isDisabled,
          })
        : customIcon

    const cloneSupplementIcon =
      typeof supplementIcon === "function"
        ? supplementIcon({
            isExpanded: isOpen,
            isDisabled,
          })
        : supplementIcon

    const cloneGeneralIcon =
      typeof generalIcon === "function"
        ? generalIcon({
            isExpanded: isOpen,
            isDisabled,
          })
        : generalIcon

    return (
      <ui.button
        {...getLabelProps(rest, ref)}
        className={cx("ui-accordion__label", className)}
        __css={css}
      >
        {children}
        {!iconHidden ? (
          <AccordionIcon>
            {cloneCustomIcon ?? cloneSupplementIcon ?? cloneGeneralIcon}
          </AccordionIcon>
        ) : null}
      </ui.button>
    )
  },
)

const AccordionIcon: FC<PropsWithChildren<IconProps>> = ({
  className,
  children,
  ...rest
}) => {
  const { isOpen, isDisabled } = useAccordionItemContext()
  const { styles } = useAccordionContext()

  const css: CSSUIObject = {
    opacity: isDisabled ? 0.4 : 1,
    transform: isOpen ? "rotate(-180deg)" : undefined,
    transition: "transform 0.2s",
    transformOrigin: "center",
    ...styles.icon,
  }

  if (children)
    return (
      <ui.span
        className={cx("ui-accordion__icon", className)}
        __css={{
          display: "inline-flex",
          justifyContent: "center",
          alignItems: "center",
          ml: "auto",
        }}
      >
        {children}
      </ui.span>
    )

  return (
    <ChevronIcon
      className={cx("ui-accordion__icon", className)}
      __css={css}
      {...rest}
    />
  )
}
import type { CSSUIObject, HTMLUIProps, UIPropGetter } from "@yamada-ui/core"
import { ui, forwardRef } from "@yamada-ui/core"
import {
  ariaAttr,
  createContext,
  cx,
  findChildren,
  getValidChildren,
  handlerAll,
  isArray,
  isEmpty,
  mergeRefs,
  omitChildren,
} from "@yamada-ui/utils"
import type { KeyboardEvent, KeyboardEventHandler, ReactNode } from "react"
import { useCallback } from "react"
import { useAccordionContext, useAccordionDescendant } from "./accordion"
import { AccordionLabel } from "./accordion-label"
import { AccordionPanel } from "./accordion-panel"

type AccordionItemContext = Omit<AccordionItemOptions, "children"> & {
  isOpen: boolean
  getLabelProps: UIPropGetter<"button">
  getPanelProps: UIPropGetter
}

const [AccordionItemProvider, useAccordionItemContext] =
  createContext<AccordionItemContext>({
    name: "AccordionItemContext",
    errorMessage: `useAccordionItemContext returned is 'undefined'. Seems you forgot to wrap the components in "<AccordionItem />"`,
  })

export { useAccordionItemContext }

type AccordionItemOptions = {
  /**
   * If `true`, the accordion item will be disabled.
   *
   * @default false
   */
  isDisabled?: boolean
  /**
   * The accordion label to use.
   */
  label?:
    | ReactNode
    | ((props: { isExpanded: boolean; isDisabled: boolean }) => ReactNode)
  /**
   * The accordion icon to use.
   */
  icon?:
    | ReactNode
    | ((props: { isExpanded: boolean; isDisabled: boolean }) => ReactNode)
  children?:
    | ReactNode
    | ((props: { isExpanded: boolean; isDisabled: boolean }) => ReactNode)
}

export type AccordionItemProps = Omit<HTMLUIProps<"li">, "children"> &
  AccordionItemOptions

export const AccordionItem = forwardRef<AccordionItemProps, "li">(
  ({ className, isDisabled = false, label, icon, children, ...rest }, ref) => {
    const { index, setIndex, setFocusedIndex, isMultiple, isToggle, styles } =
      useAccordionContext()

    const {
      index: i,
      register,
      descendants,
    } = useAccordionDescendant({ disabled: isDisabled })

    const isOpen =
      i !== -1 ? (isArray(index) ? index.includes(i) : index === i) : false

    if (isOpen && isDisabled)
      console.warn(`Accordion: Cannot open a disabled accordion item`)

    const onChange = useCallback(
      (isOpen: boolean) => {
        if (i === -1) return

        if (isMultiple && isArray(index)) {
          setIndex(isOpen ? index.concat(i) : index.filter((v) => v !== i))
        } else if (isOpen) {
          setIndex(i)
        } else if (isToggle) {
          setIndex(-1)
        }
      },
      [isMultiple, isToggle, i, index, setIndex],
    )

    const onFocus = useCallback(() => {
      setFocusedIndex(i)
    }, [setFocusedIndex, i])

    const onClick = useCallback(() => {
      onChange(!isOpen)
      setFocusedIndex(i)
    }, [i, setFocusedIndex, isOpen, onChange])

    const onKeyDown = useCallback(
      (ev: KeyboardEvent) => {
        const actions: Record<string, KeyboardEventHandler> = {
          ArrowDown: () => {
            const next = descendants.enabledNextValue(i)

            next?.node.focus()
          },
          ArrowUp: () => {
            const prev = descendants.enabledPrevValue(i)

            prev?.node.focus()
          },
          Home: () => {
            const first = descendants.enabledFirstValue()

            first?.node.focus()
          },
          End: () => {
            const last = descendants.enabledLastValue()

            last?.node.focus()
          },
        }

        const action = actions[ev.key]

        if (!action) return

        ev.preventDefault()
        action(ev)
      },
      [descendants, i],
    )

    const getLabelProps: UIPropGetter<"button"> = useCallback(
      (props = {}, ref = null) => ({
        ...props,
        ref: mergeRefs(register, ref),
        type: "button",
        disabled: isDisabled,
        "aria-expanded": ariaAttr(isOpen),
        onClick: handlerAll(props.onClick, onClick),
        onFocus: handlerAll(props.onFocus, onFocus),
        onKeyDown: handlerAll(props.onKeyDown, onKeyDown),
      }),
      [isDisabled, isOpen, onClick, onFocus, onKeyDown, register],
    )

    const getPanelProps: UIPropGetter = useCallback(
      (props = {}, ref = null) => ({ ...props, ref }),
      [],
    )

    const css: CSSUIObject = { ...styles.item, overflowAnchor: "none" }

    const cloneLabel =
      typeof label === "function"
        ? label({
            isExpanded: isOpen,
            isDisabled,
          })
        : label

    if (typeof children === "function")
      children = children({ isExpanded: isOpen, isDisabled })

    const validChildren = getValidChildren(children)

    const [customAccordionLabel] = findChildren(validChildren, AccordionLabel)
    const [customAccordionPanel] = findChildren(validChildren, AccordionPanel)

    const cloneChildren = !isEmpty(validChildren)
      ? omitChildren(validChildren, AccordionLabel, AccordionPanel)
      : children

    return (
      <AccordionItemProvider
        value={{ isOpen, isDisabled, icon, getLabelProps, getPanelProps }}
      >
        <ui.li
          ref={ref}
          className={cx("ui-accordion__item", className)}
          aria-expanded={ariaAttr(isOpen)}
          __css={css}
          {...rest}
        >
          {customAccordionLabel ?? (
            <AccordionLabel>{cloneLabel}</AccordionLabel>
          )}
          {customAccordionPanel ?? (
            <AccordionPanel>{cloneChildren}</AccordionPanel>
          )}
        </ui.li>
      </AccordionItemProvider>
    )
  },
)
import type { CSSUIObject, HTMLUIProps } from "@yamada-ui/core"
import { ui, forwardRef } from "@yamada-ui/core"
import type { WithTransitionProps } from "@yamada-ui/motion"
import type { CollapseProps } from "@yamada-ui/transitions"
import { Collapse } from "@yamada-ui/transitions"
import { cx } from "@yamada-ui/utils"
import { useAccordionContext } from "./accordion"
import { useAccordionItemContext } from "./accordion-item"

export type AccordionPanelProps = Omit<
  WithTransitionProps<HTMLUIProps<"div">>,
  "isOpen"
> &
  Pick<CollapseProps, "animationOpacity" | "startingHeight" | "endingHeight">

export const AccordionPanel = forwardRef<AccordionPanelProps, "div">(
  (
    {
      className,
      unmountOnExit,
      animationOpacity,
      startingHeight,
      endingHeight,
      transition,
      transitionEnd,
      delay,
      duration,
      children,
      ...rest
    },
    ref,
  ) => {
    const { isOpen, getPanelProps } = useAccordionItemContext()
    const { styles } = useAccordionContext()

    const resolvedChildren =
      typeof children === "string" ? <p>{children}</p> : children

    const css: CSSUIObject = { ...styles.panel }

    return (
      <Collapse
        {...{
          isOpen,
          unmountOnExit,
          animationOpacity,
          startingHeight,
          endingHeight,
          transition,
          transitionEnd,
          delay,
          duration,
        }}
      >
        <ui.div
          {...getPanelProps(rest, ref)}
          className={cx("ui-accordion__panel", className)}
          __css={css}
        >
          {resolvedChildren}
        </ui.div>
      </Collapse>
    )
  },
)
