import type { CSSUIObject, HTMLUIProps, ThemeProps } from "@yamada-ui/core"
import {
  ui,
  forwardRef,
  useMultiComponentStyle,
  omitThemeProps,
} from "@yamada-ui/core"
import type { MotionPanInfo } from "@yamada-ui/motion"
import type { SlideProps } from "@yamada-ui/transitions"
import { Slide } from "@yamada-ui/transitions"
import { useValue } from "@yamada-ui/use-value"
import {
  createContext,
  getValidChildren,
  findChildren,
  cx,
  isArray,
} from "@yamada-ui/utils"
import type { FC } from "react"
import { useCallback, useMemo } from "react"
import { useModal } from "./modal"
import type {
  ModalProps,
  ModalOverlayProps,
  ModalCloseButtonProps,
  ModalHeaderProps,
  ModalBodyProps,
  ModalFooterProps,
} from "./"
import {
  Modal,
  ModalOverlay,
  ModalCloseButton,
  ModalHeader,
  ModalBody,
  ModalFooter,
} from "./"

type DrawerOptions = {
  /**
   * The placement of the drawer.
   *
   * @default 'right'
   */
  placement?: SlideProps["placement"]
  /**
   * If `true` and drawer's placement is `top` or `bottom`, the drawer will occupy the viewport height (100dvh).
   */
  isFullHeight?: boolean
  /**
   * If `true`, then the drawer will close on drag.
   *
   * @default false
   */
  closeOnDrag?: boolean
  /**
   * If `true`, display the drag bar when `closeOnDrag` is `true`.
   *
   * @default true
   */
  withDragBar?: boolean
  /**
   * Applies constraints on the permitted draggable area.
   *
   * @default 0
   */
  dragConstraints?: number
  /**
   * The degree of movement allowed outside constraints. 0 = no movement, 1 = full movement.
   *
   * @default 0.1
   */
  dragElastic?: number
  /**
   * Offset from being dragged to closing.
   *
   * @default 80
   */
  dragOffset?: number
  /**
   * Velocity of the drag that triggers close.
   *
   * @default 100
   */
  dragVelocity?: number
  /**
   * Props for the blank area when `closeOnDrag` is `true`.
   */
  blankForDragProps?: CSSUIObject
}

export type DrawerProps = Omit<
  ModalProps,
  | "scrollBehavior"
  | "animation"
  | "outside"
  | "placement"
  | "dragConstraints"
  | "dragElastic"
  | keyof ThemeProps
> &
  ThemeProps<"Drawer"> &
  DrawerOptions

type DrawerContext = Record<string, CSSUIObject>

const [DrawerProvider, useDrawer] = createContext<DrawerContext>({
  name: `DrawerContext`,
  errorMessage: `useDrawer returned is 'undefined'. Seems you forgot to wrap the components in "<Drawer />" `,
})

/**
 * `Drawer` is a component for a panel that appears from the edge of the screen.
 *
 * @see Docs https://yamada-ui.com/components/overlay/drawer
 */
export const Drawer = forwardRef<DrawerProps, "div">(
  ({ size, placement = "right", closeOnDrag = false, ...props }, ref) => {
    const [styles, mergedProps] = useMultiComponentStyle("Drawer", {
      size,
      placement,
      closeOnDrag,
      ...props,
    })
    const {
      children,
      isOpen,
      onClose,
      onOverlayClick,
      onEsc,
      onCloseComplete,
      withCloseButton = !closeOnDrag,
      withOverlay = true,
      withDragBar = true,
      allowPinchZoom,
      autoFocus,
      restoreFocus,
      initialFocusRef,
      finalFocusRef,
      blockScrollOnMount,
      closeOnOverlay,
      closeOnEsc,
      lockFocusAcrossFrames,
      duration = { enter: 0.4, exit: 0.3 },
      dragConstraints = 0,
      dragElastic = 0.1,
      dragOffset = 80,
      dragVelocity = 100,
      blankForDragProps,
      portalProps,
      ...rest
    } = omitThemeProps(mergedProps, ["isFullHeight"])

    const validChildren = getValidChildren(children)

    const [customDrawerOverlay, ...cloneChildren] = findChildren(
      validChildren,
      DrawerOverlay,
    )

    return (
      <DrawerProvider value={styles}>
        <Modal
          ref={ref}
          {...{
            isOpen,
            onClose,
            onOverlayClick,
            onEsc,
            onCloseComplete,
            withCloseButton: false,
            withOverlay: false,
            allowPinchZoom,
            autoFocus,
            restoreFocus,
            initialFocusRef,
            finalFocusRef,
            blockScrollOnMount,
            closeOnOverlay,
            closeOnEsc,
            lockFocusAcrossFrames,
            duration,
            portalProps,
          }}
        >
          {customDrawerOverlay ?? (withOverlay ? <DrawerOverlay /> : null)}

          <DrawerContent
            {...{
              dragConstraints,
              dragElastic,
              dragOffset,
              dragVelocity,
              withCloseButton,
              withDragBar,
              blankForDragProps,
              ...rest,
              placement,
              closeOnDrag,
            }}
          >
            {cloneChildren}
          </DrawerContent>
        </Modal>
      </DrawerProvider>
    )
  },
)

type DrawerContentProps = Omit<
  DrawerProps,
  "color" | "transition" | "isOpen" | keyof ThemeProps
> &
  Required<
    Pick<
      DrawerProps,
      | "placement"
      | "dragConstraints"
      | "dragElastic"
      | "dragOffset"
      | "dragVelocity"
    >
  >

export const DrawerContent = forwardRef<DrawerContentProps, "div", false>(
  (
    {
      className,
      children,
      placement: _placement,
      withCloseButton,
      withDragBar,
      closeOnDrag,
      dragConstraints,
      dragElastic,
      dragOffset,
      dragVelocity,
      blankForDragProps,
      ...rest
    },
    ref,
  ) => {
    const { isOpen, onClose, duration } = useModal()
    const styles = useDrawer()
    const placement = useValue(_placement)

    const validChildren = getValidChildren(children)

    const [customDrawerCloseButton, ...cloneChildren] = findChildren(
      validChildren,
      DrawerCloseButton,
    )

    const blankForDragBg = useMemo(() => {
      const propBg =
        rest.backgroundColor ?? rest.bgColor ?? rest.background ?? rest.bg
      const styleBg =
        styles.container?.backgroundColor ??
        styles.container?.bgColor ??
        styles.container?.background ??
        styles.container?.bg
      const computedBg = propBg ?? styleBg

      return isArray(computedBg) ? computedBg : [computedBg]
    }, [rest, styles])

    const blankForDrag = useMemo<CSSUIObject>(() => {
      let position: CSSUIObject = {}

      switch (placement) {
        case "top":
          position = { top: "calc(-100dvh + 1px)", left: 0, right: 0 }
          break

        case "bottom":
          position = { bottom: "calc(-100dvh + 1px)", left: 0, right: 0 }
          break

        case "left":
          position = { left: "calc(-100% + 1px)", top: 0, bottom: 0 }
          break

        case "right":
          position = { right: "calc(-100% + 1px)", top: 0, bottom: 0 }
          break
      }

      const [lightBg, darkBg] = blankForDragBg

      return {
        _after: {
          content: '""',
          display: "block",
          w: "100%",
          h: "100dvh",
          bg: lightBg,
          position: "absolute",
          ...position,
          ...blankForDragProps,
        },
        _dark: {
          _after: {
            bg: darkBg,
          },
        },
      }
    }, [placement, blankForDragBg, blankForDragProps])

    const css = useMemo<CSSUIObject>(
      () => ({
        display: "flex",
        flexDirection:
          placement === "top" || placement === "bottom" ? "column" : "row",
        outline: 0,
        ...(closeOnDrag ? blankForDrag : {}),
        ...styles.container,
      }),
      [blankForDrag, closeOnDrag, placement, styles],
    )

    const getDragDirectionRestriction = useCallback(
      (value: number) => {
        switch (placement) {
          case "top":
            return { bottom: value }
          case "bottom":
            return { top: value }
          case "left":
            return { right: value }
          case "right":
            return { left: value }
        }
      },
      [placement],
    )

    const getDragDirection = useCallback(() => {
      switch (placement) {
        case "top":
        case "bottom":
          return "y"
        case "left":
        case "right":
          return "x"
      }
    }, [placement])

    const isCloseByDragInfo = useCallback(
      (info: MotionPanInfo) => {
        switch (placement) {
          case "top":
            return (
              info.velocity.y <= dragVelocity * -1 ||
              info.offset.y <= dragOffset * -1
            )
          case "bottom":
            return (
              info.velocity.y >= dragVelocity || info.offset.y >= dragOffset
            )
          case "left":
            return (
              info.velocity.x <= dragVelocity * -1 ||
              info.offset.x <= dragOffset * -1
            )
          case "right":
            return (
              info.velocity.x >= dragVelocity || info.offset.x >= dragOffset
            )
        }
      },
      [placement, dragVelocity, dragOffset],
    )

    return (
      <Slide
        ref={ref}
        className={cx("ui-drawer", className)}
        isOpen={isOpen}
        placement={placement}
        duration={duration}
        drag={closeOnDrag ? getDragDirection() : false}
        dragConstraints={getDragDirectionRestriction(dragConstraints)}
        dragElastic={getDragDirectionRestriction(dragElastic)}
        dragSnapToOrigin
        dragMomentum={false}
        onDragEnd={(_, info) => {
          if (isCloseByDragInfo(info)) onClose?.()
        }}
        tabIndex={-1}
        __css={css}
        {...rest}
      >
        {customDrawerCloseButton ??
          (withCloseButton && onClose ? <DrawerCloseButton /> : null)}

        {withDragBar &&
        closeOnDrag &&
        (placement === "bottom" || placement === "right") ? (
          <DrawerDragBar />
        ) : null}

        <ui.div
          className="ui-drawer__inner"
          __css={{ display: "flex", flexDirection: "column", ...styles.inner }}
        >
          {cloneChildren}
        </ui.div>

        {withDragBar &&
        closeOnDrag &&
        (placement === "top" || placement === "left") ? (
          <DrawerDragBar />
        ) : null}
      </Slide>
    )
  },
)

export type DrawerOverlayProps = ModalOverlayProps

export const DrawerOverlay = forwardRef<DrawerOverlayProps, "div">(
  ({ className, ...rest }, ref) => {
    const styles = useDrawer()

    const css: CSSUIObject = { ...styles.overlay }

    return (
      <ModalOverlay
        ref={ref}
        className={cx("ui-drawer__overlay", className)}
        __css={css}
        {...rest}
      />
    )
  },
)

export type DrawerDragBarProps = HTMLUIProps<"div">

export const DrawerDragBar: FC<DrawerDragBarProps> = ({
  className,
  ...rest
}) => {
  const styles = useDrawer()

  const css: CSSUIObject = { ...styles.dragBar }

  return (
    <ui.div
      className={cx("ui-drawer__drag-bar", className)}
      __css={css}
      {...rest}
    />
  )
}

export type DrawerCloseButtonProps = ModalCloseButtonProps

export const DrawerCloseButton = forwardRef<DrawerCloseButtonProps, "button">(
  ({ className, ...rest }, ref) => {
    const styles = useDrawer()

    const css: CSSUIObject = { ...styles.closeButton }

    return (
      <ModalCloseButton
        ref={ref}
        className={cx("ui-drawer__close-button", className)}
        __css={css}
        {...rest}
      />
    )
  },
)

export type DrawerHeaderProps = ModalHeaderProps

export const DrawerHeader = forwardRef<DrawerHeaderProps, "header">(
  ({ className, ...rest }, ref) => {
    const styles = useDrawer()

    const css: CSSUIObject = { ...styles.header }

    return (
      <ModalHeader
        ref={ref}
        className={cx("ui-drawer__header", className)}
        __css={css}
        {...rest}
      />
    )
  },
)

export type DrawerBodyProps = ModalBodyProps

export const DrawerBody = forwardRef<DrawerBodyProps, "main">(
  ({ className, ...rest }, ref) => {
    const styles = useDrawer()

    const css: CSSUIObject = { ...styles.body }

    return (
      <ModalBody
        ref={ref}
        className={cx("ui-drawer__body", className)}
        __css={css}
        {...rest}
      />
    )
  },
)

export type DrawerFooterProps = ModalFooterProps

export const DrawerFooter = forwardRef<DrawerFooterProps, "footer">(
  ({ className, ...rest }, ref) => {
    const styles = useDrawer()

    const css: CSSUIObject = { ...styles.footer }

    return (
      <ModalFooter
        ref={ref}
        className={cx("ui-drawer__footer", className)}
        __css={css}
        {...rest}
      />
    )
  },
)
import type {
  ThemeProps,
  CSSUIObject,
  CSSUIProps,
  Token,
} from "@yamada-ui/core"
import {
  ui,
  forwardRef,
  omitThemeProps,
  useMultiComponentStyle,
} from "@yamada-ui/core"
import type { FocusLockProps } from "@yamada-ui/focus-lock"
import { FocusLock } from "@yamada-ui/focus-lock"
import type { MotionProps, MotionTransitionProperties } from "@yamada-ui/motion"
import { AnimatePresence, Motion } from "@yamada-ui/motion"
import type { PortalProps } from "@yamada-ui/portal"
import { Portal } from "@yamada-ui/portal"
import { scaleFadeProps, slideFadeProps } from "@yamada-ui/transitions"
import { useValue } from "@yamada-ui/use-value"
import {
  cx,
  createContext,
  getValidChildren,
  findChildren,
} from "@yamada-ui/utils"
import type { KeyboardEvent } from "react"
import { cloneElement, useCallback } from "react"
import { RemoveScroll } from "react-remove-scroll"
import { DrawerContent } from "./drawer"
import {
  DrawerOverlay,
  DialogOverlay,
  DialogCloseButton,
  ModalOverlay,
  ModalCloseButton,
} from "./"

type ModalContext = ModalOptions & {
  styles: Record<string, CSSUIObject>
}

const [ModalProvider, useModal] = createContext<ModalContext>({
  name: `ModalContext`,
  errorMessage: `useModal returned is 'undefined'. Seems you forgot to wrap the components in "<Modal />" `,
})

export { useModal }

type ModalOptions = Pick<
  FocusLockProps,
  | "autoFocus"
  | "initialFocusRef"
  | "finalFocusRef"
  | "restoreFocus"
  | "lockFocusAcrossFrames"
> & {
  /**
   * If `true`, the open will be opened.
   */
  isOpen: boolean
  /**
   * Callback invoked to close the modal.
   */
  onClose?: () => void
  /**
   * Callback fired when the overlay is clicked.
   */
  onOverlayClick?: () => void
  /**
   * Callback fired when the escape key is pressed and focus is within modal.
   */
  onEsc?(): void
  /**
   * Callback function to run side effects after the modal has closed.
   */
  onCloseComplete?: () => void
  /**
   * The placement of the modal.
   *
   * @default 'center'
   */
  placement?: Token<
    | "center"
    | "top"
    | "right"
    | "bottom"
    | "left"
    | "top-left"
    | "top-right"
    | "bottom-left"
    | "bottom-right"
  >
  /**
   * The CSS `padding` property.
   */
  outside?: CSSUIProps["p"]
  /**
   * If `true`, display the modal close button.
   *
   * @default true
   */
  withCloseButton?: boolean
  /**
   * If `true`, display the modal overlay.
   *
   * @default true
   */
  withOverlay?: boolean
  /**
   * Handle zoom or pinch gestures on iOS devices when scroll locking is enabled.
   *
   * @default false.
   */
  allowPinchZoom?: boolean
  /**
   * Where scroll behavior should originate.
   *
   * - `inside`: scroll only occurs within the `ModalBody`.
   * - `outside`: the entire `ModalContent` will scroll within the viewport.
   *
   * @default 'inside'
   */
  scrollBehavior?: "inside" | "outside"
  /**
   * If `true`, scrolling will be disabled on the `body` when the modal opens.
   *
   * @default true
   */
  blockScrollOnMount?: boolean
  /**
   * If `true`, the modal will close when the overlay is clicked.
   *
   * @default true
   */
  closeOnOverlay?: boolean
  /**
   * If `true`, the modal will close when the `Esc` key is pressed.
   *
   * @default true
   */
  closeOnEsc?: boolean
  /**
   * The animation of the tooltip.
   *
   * @default 'scale'
   */
  animation?: "scale" | "top" | "right" | "left" | "bottom" | "none"
  /**
   * The animation duration.
   */
  duration?: MotionTransitionProperties["duration"]
  /**
   * Props to be forwarded to the portal component.
   */
  portalProps?: Omit<PortalProps, "children">
}

export type ModalProps = Omit<
  MotionProps<"section">,
  "scrollBehavior" | "animation" | "color" | "transition"
> &
  ThemeProps<"Modal"> &
  ModalOptions

/**
 * `Modal` is a component that is displayed over the main content to focus the user's attention solely on the information.
 *
 * @see Docs https://yamada-ui.com/components/overlay/modal
 */
export const Modal = forwardRef<ModalProps, "section">(
  ({ size, ...props }, ref) => {
    const [styles, mergedProps] = useMultiComponentStyle("Modal", {
      size,
      ...props,
    })
    const {
      className,
      children,
      isOpen,
      onClose,
      onOverlayClick,
      onEsc,
      onCloseComplete,
      placement: _placement = "center",
      outside = "fallback(4, 1rem)",
      withCloseButton = true,
      withOverlay = true,
      allowPinchZoom = false,
      scrollBehavior = "inside",
      autoFocus,
      restoreFocus,
      initialFocusRef,
      finalFocusRef,
      blockScrollOnMount = true,
      closeOnOverlay = true,
      closeOnEsc = true,
      lockFocusAcrossFrames = true,
      animation = "scale",
      duration,
      portalProps,
      ...rest
    } = omitThemeProps(mergedProps)

    const onKeyDown = useCallback(
      (ev: KeyboardEvent) => {
        if (ev.key !== "Escape") return

        ev.stopPropagation()

        if (closeOnEsc) onClose?.()

        onEsc?.()
      },
      [closeOnEsc, onClose, onEsc],
    )

    const validChildren = getValidChildren(children)

    const [customModalOverlay, ...cloneChildren] = findChildren(
      validChildren,
      ModalOverlay,
      DialogOverlay,
      DrawerOverlay,
    )

    let [drawerContent] = findChildren(validChildren, DrawerContent)

    if (drawerContent)
      drawerContent = cloneElement(drawerContent, { onKeyDown })

    const placement = useValue(_placement)

    const css: CSSUIObject = {
      position: "fixed",
      top: 0,
      left: 0,
      zIndex: "fallback(jeice, 110)",
      w: "100vw",
      h: "100dvh",
      p: size !== "full" ? outside : undefined,
      display: "flex",
      justifyContent: placement.includes("left")
        ? "flex-start"
        : placement.includes("right")
          ? "flex-end"
          : "center",
      alignItems: placement.includes("top")
        ? "flex-start"
        : placement.includes("bottom")
          ? "flex-end"
          : "center",
    }

    return (
      <ModalProvider
        value={{
          isOpen,
          onClose,
          onOverlayClick,
          withCloseButton,
          scrollBehavior,
          closeOnOverlay,
          animation,
          duration,
          styles,
        }}
      >
        <AnimatePresence onExitComplete={onCloseComplete}>
          {isOpen ? (
            <Portal {...portalProps}>
              <FocusLock
                autoFocus={autoFocus}
                initialFocusRef={initialFocusRef}
                finalFocusRef={finalFocusRef}
                restoreFocus={restoreFocus}
                lockFocusAcrossFrames={lockFocusAcrossFrames}
              >
                <RemoveScroll
                  allowPinchZoom={allowPinchZoom}
                  enabled={blockScrollOnMount}
                  forwardProps
                >
                  <ui.div __css={css}>
                    {customModalOverlay ??
                      (withOverlay && size !== "full" ? (
                        <ModalOverlay />
                      ) : null)}

                    {drawerContent ?? (
                      <ModalContent
                        ref={ref}
                        className={className}
                        onKeyDown={onKeyDown}
                        {...rest}
                      >
                        {cloneChildren}
                      </ModalContent>
                    )}
                  </ui.div>
                </RemoveScroll>
              </FocusLock>
            </Portal>
          ) : null}
        </AnimatePresence>
      </ModalProvider>
    )
  },
)

type ModalContentProps = Omit<
  MotionProps<"section">,
  "scrollBehavior" | "animation" | "color" | "transition"
>

const getModalContentProps = (
  animation: ModalProps["animation"] = "scale",
  duration?: MotionTransitionProperties["duration"],
) => {
  switch (animation) {
    case "scale":
      return {
        ...scaleFadeProps,
        custom: { scale: 0.95, reverse: true, duration },
      }
    case "top":
      return {
        ...slideFadeProps,
        custom: { offsetY: -16, reverse: true, duration },
      }
    case "right":
      return {
        ...slideFadeProps,
        custom: { offsetX: 16, reverse: true, duration },
      }
    case "left":
      return {
        ...slideFadeProps,
        custom: { offsetX: -16, reverse: true, duration },
      }
    case "bottom":
      return {
        ...slideFadeProps,
        custom: { offsetY: 16, reverse: true, duration },
      }
  }
}

const ModalContent = forwardRef<ModalContentProps, "section", false>(
  ({ className, children, __css, ...rest }, ref) => {
    const {
      styles,
      scrollBehavior,
      withCloseButton,
      onClose,
      animation,
      duration,
    } = useModal()

    const validChildren = getValidChildren(children)

    const [customModalCloseButton, ...cloneChildren] = findChildren(
      validChildren,
      ModalCloseButton,
      DialogCloseButton,
    )

    const props =
      animation !== "none" ? getModalContentProps(animation, duration) : {}

    const css: CSSUIObject = {
      position: "relative",
      maxH: "100%",
      display: "flex",
      flexDirection: "column",
