import React from 'react';
import { Link } from 'react-router-dom'; // Linkを使用して遷移を実現
import { Input, Button, FormControl, Label } from '@yamada-ui/react';

function Signin() {
    return (
        <div className="slideshow bg-gradient-to-b from-transparent to-black via-transparent opacity-75 fixed inset-0 z-0">
            <div className="max-w-md w-full mx-auto p-4 mt-20 bg-white bg-opacity-50 rounded-lg shadow-lg">
                <FormControl>
                    <Label>ユーザーID</Label>
                    <Input placeholder="ユーザーID" className="mb-4" />
                    <Label>パスワード</Label>
                    <Input type="password" placeholder="パスワード" className="mb-4" />
                    <Button>ログイン</Button>
                    <div className="mt-4">
                        <Link to="/signup" className="text-blue-500 hover:underline">新規登録はこちら</Link>
                    </div>
                </FormControl>
            </div>
        </div>
    );
}
