import React from 'react';
import { Button, Input } from '@yamada-ui/react';
import { useLoading } from '@yamada-ui/react';

function Signup() {
  const { screen } = useLoading();

  const handleSignup = async (event) => {
    event.preventDefault();
    screen.start();

    try {
      await new Promise(resolve => setTimeout(resolve, 2000)); // サンプルの遅延シミュレーション
      // 実際のサインアップロジックをここに追加
    } finally {
      screen.finish();
    }
  };

  return (
    <div>
      <form onSubmit={handleSignup}>
        <Input type='text' placeholder='ユーザー名' />
        <Input type='password' placeholder='パスワード' />
        <Input type='password' placeholder='パスワード確認' />
        <Button type='submit'>登録</Button>
      </form>
    </div>
  );
}

export default Signup;
