import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import {
  Button,
  Input,
  FormControl,
  Label,
  HelperMessage,
  useLoading,
  Modal,
  ModalOverlay,
  ModalCloseButton,
  ModalHeader,
  ModalBody,
  ModalFooter,
  useDisclosure
} from '@yamada-ui/react';

function Signin() {
    const { screen } = useLoading();
    const [isSubmitting, setSubmitting] = useState(false);
    const [userID, setUserID] = useState('');
    const [password, setPassword] = useState('');
    const { isOpen, onOpen, onClose } = useDisclosure();

    const handleLogin = async (event) => {
        event.preventDefault();
        if (!userID || !password) {
            onOpen();
            return;
        }
        setSubmitting(true);
        screen.start();

        try {
            await new Promise(resolve => setTimeout(resolve, 2000)); // サンプルの遅延シミュレーション
            // 実際のログインロジックをここに追加
        } finally {
            setSubmitting(false);
            screen.finish();
        }
    };

    return (
        <div>
            <form onSubmit={handleLogin}>
                <FormControl isRequired label="ユーザーID" errorMessage={!userID && "ユーザーIDは必須です。"}>
                    <Label>ユーザーID</Label>
                    <Input type='text' placeholder='ユーザーID' value={userID} onChange={(e) => setUserID(e.target.value)} />
                    <HelperMessage>IDを入力してください。</HelperMessage>
                </FormControl>
                <FormControl isRequired label="パスワード" errorMessage={!password && "パスワードは必須です。"}>
                    <Label>パスワード</Label>
                    <Input type='password' placeholder='パスワード' value={password} onChange={(e) => setPassword(e.target.value)} />
                </FormControl>
                <Button type='submit' isLoading={isSubmitting} loadingText="ログイン中...">ログイン</Button>
            </form>

            <Link to="/resetpass">パスワードをお忘れの方</Link>
            <Link to="/signup"><Button>新規登録</Button></Link>

            <Modal isOpen={isOpen} onClose={onClose}>
                <ModalOverlay />
                <ModalCloseButton />
                <ModalHeader>入力エラー</ModalHeader>
                <ModalBody>すべての必須項目を入力してください。</ModalBody>
                <ModalFooter>
                    <Button onClick={onClose}>閉じる</Button>
                </ModalFooter>
            </Modal>
        </div>
    );
}

export default Signin;
