  string,
  colors,
}: { string?: string; colors?: string[] } = {}) => {
  const fallback = randomHex()

  if (string && colors) return randomColorFromList(string, colors)

  if (string && !colors) return randomColorFromString(string)

  if (colors && !string) return randomFromList(colors)

  return fallback
}

const randomHex = () =>
  `#${Math.floor(Math.random() * 0xffffff)
    .toString(16)
    .padEnd(6, "0")}`

const randomColorFromString = (str: string) => {
  let hash = 0

  if (str.length === 0) return hash.toString()

  for (let i = 0; i < str.length; i += 1) {
    hash = str.charCodeAt(i) + ((hash << 5) - hash)
    hash = hash & hash
  }

  let color = "#"

  for (let j = 0; j < 3; j += 1) {
    const value = (hash >> (j * 8)) & 255

    color += `00${value.toString(16)}`.substr(-2)
  }

  return color
}

const randomColorFromList = (str: string, list: string[]) => {
  let index = 0

  if (str.length === 0) return list[0]

  for (let i = 0; i < str.length; i += 1) {
    index = str.charCodeAt(i) + ((index << 5) - index)

    index = index & index
  }

  index = ((index % list.length) + list.length) % list.length

  return list[index]
}

const randomFromList = (list: string[]) =>
  list[Math.floor(Math.random() * list.length)]

const getBrightness = (color: string) => {
  const [r, g, b] = c.parseToRgba(color)

  return (r * 299 + g * 587 + b * 114) / 1000
}

export const isTone =
  (color: string) => (theme?: Dict, colorMode?: ColorMode) => {
    const raw = theme ? getColor(color)(theme, colorMode) : color

    const brightness = getBrightness(raw)

    const isDark = brightness < 128

    return isDark ? "dark" : "light"
  }

export const isLight =
  (color: string) => (theme?: Dict, colorMode?: ColorMode) =>
    isTone(color)(theme, colorMode) === "dark"

export const isDark =
  (color: string) => (theme?: Dict, colorMode?: ColorMode) =>
    isTone(color)(theme, colorMode) === "light"

export const convertColor =
  (color: string, fallback?: string) =>
  (format: ColorFormat): string | undefined => {
    try {
      const isAlpha = format.endsWith("a")

      if (/^[0-9a-fA-F]{6}$/.test(color)) color = "#" + color

      if (format.startsWith("hex")) {
        let hexa = c.toHex(color)

        if (isAlpha) {
          if (hexa.length === 7) hexa += "ff"
        } else {
          hexa = hexa.replace(/(?<=^#([0-9a-fA-F]{6}))[0-9a-fA-F]{2}$/, "")
        }

        return hexa
      } else if (format.startsWith("hsl")) {
        let hsla = c.toHsla(color)

        if (!isAlpha) {
          hsla = hsla.replace(/hsla/, "hsl")
          hsla = hsla.replace(/,\s*\d+(\.\d+)?\)$/, ")")
        }

        return hsla
      } else {
        let rgba = c.toRgba(color)

        if (!isAlpha) {
          rgba = rgba.replace(/rgba/, "rgb")
          rgba = rgba.replace(/,\s*\d+(\.\d+)?\)$/, ")")
        }

        return rgba
      }
    } catch {
      if (fallback) return convertColor(fallback)(format)
    }
  }

export const calcFormat = (color: string): ColorFormat => {
  if (color.startsWith("hsl")) {
    return color.startsWith("hsla") ? "hsla" : "hsl"
  } else if (color.startsWith("rgb")) {
    return color.startsWith("rgba") ? "rgba" : "rgb"
  } else {
    return color.length === 9 ? "hexa" : "hex"
  }
}

export const getAlpha = (color: string) => c.parseToRgba(color)[3]

export const alphaToHex = (a: number) => {
  if (0 > a) a = 0
  if (1 < a) a = 1

  return Math.round(a * 255)
    .toString(16)
    .padStart(2, "0")
}

export const parseToRgba = (color: string, fallback?: string) => {
  try {
    if (/^[0-9a-fA-F]{6}$/.test(color)) color = "#" + color

    return c.parseToRgba(color)
  } catch {
    if (fallback) return c.parseToRgba(fallback)
  }
}

export const parseToHsla = (color: string, fallback?: string) => {
  try {
    if (/^[0-9a-fA-F]{6}$/.test(color)) color = "#" + color

    return c.parseToHsla(color)
  } catch {
    if (fallback) return c.parseToHsla(fallback)
  }
}

export const parseToHsv = (
  color: string,
  fallback?: string,
): [number, number, number, number] => {
  let [r, g, b, a] = parseToRgba(color, fallback) ?? [255, 255, 255, 1]

  r = r / 255
  g = g / 255
  b = b / 255

  const [min, max] = [Math.min(r, g, b), Math.max(r, g, b)]
  const delta = max - min

  let [h, v, s] = [0, max, max == 0 ? 0 : delta / max]

  switch (min) {
    case max:
      h = 0
      break

    case r:
      h = 60 * ((b - g) / delta) + 180
      break

    case g:
      h = 60 * ((r - b) / delta) + 300
      break

    case b:
      h = 60 * ((g - r) / delta) + 60
      break
  }

  return [h, s, v, a]
}

export const rgbaTo =
  ([r, g, b, a]: [number, number, number, number], fallback?: string) =>
  (format: ColorFormat = "hex") =>
    convertColor(c.rgba(r, g, b, a), fallback)(format)

export const hslaTo =
  ([h, s, l, a]: [number, number, number, number], fallback?: string) =>
  (format: ColorFormat = "hex") =>
    convertColor(c.hsla(h, s, l, a), fallback)(format)

export const hsvTo =
  ([h, s, v, a]: [number, number, number, number?], fallback?: string) =>
  (format: ColorFormat = "hex"): string | undefined => {
    h = h / 60

    let rgb: [number, number, number] = [v, v, v]

    let i = Math.floor(h)
    let f = h - i
    let p = v * (1 - s)
    let q = v * (1 - s * f)
    let t = v * (1 - s * (1 - f))

    switch (i) {
      case 0:
      case 6:
        rgb = [v, t, p]
        break

      case 1:
        rgb = [q, v, p]
        break

      case 2:
        rgb = [p, v, t]
        break

      case 3:
        rgb = [p, q, v]
        break

      case 4:
        rgb = [t, p, v]
        break

      case 5:
        rgb = [v, p, q]
        break
    }

    let color = `rgb(${rgb.map((v) => Math.round(v * 255)).join(", ")})`

    if (isNumber(a)) color = color.replace(/\)$/, `, ${a})`)

    return convertColor(color, fallback)(format)
  }

export const sameColor = (
  color: string | undefined,
  comparison: string | undefined,
) => {
  if (!color) return false
  if (!comparison) return false

  const a = parseToRgba(color) ?? []
  const b = parseToRgba(comparison) ?? []

  return a[0] === b[0] && a[1] === b[1] && a[2] === b[2] && a[3] === b[3]
}
import type { Dict, Union } from "."
import { isObject, isArray, isFunction, isString } from "."

const omitObjectHelper = <T extends Dict, K extends keyof T>(
  obj: T,
  path: Union<K>[],
): any => {
  if (!path.length) return obj

  const [primaryKey, ...restKeys] = path

  if (restKeys.length === 0 && primaryKey in obj) {
    const { [primaryKey]: _, ...rest } = obj

    return rest
  }

  if (obj[primaryKey] && isObject(obj[primaryKey])) {
    return {
      ...obj,
      [primaryKey]: omitObjectHelper(obj[primaryKey], restKeys),
    }
  }

  return obj
}

export const omitObject = <
  Y extends Dict,
  M extends keyof Y,
  D extends unknown,
>(
  obj: Y,
  keys: Union<M>[],
) => {
  return keys.reduce((prev, key) => {
    const path = (isString(key) ? key.split(".") : []) as Union<M>[]

    return omitObjectHelper(prev, path)
  }, obj) as unknown as D extends unknown ? Omit<Y, M> : D
}

export const pickObject = <
  Y extends Dict,
  M extends keyof Y,
  D extends unknown,
>(
  obj: Y,
  keys: Union<M>[],
  fallbackValue = "__fallback",
) =>
  keys.reduce((prev, key) => {
    const path = isString(key) ? key.split(".") : []

    if (!path.length) return prev

    const value = getMemoizedObject(obj, key as string, fallbackValue)

    if (value === fallbackValue) return prev

    prev = merge(
      prev,
      path.reduceRight(
        (prev, key) => ({ [key]: key === path.at(-1) ? value : prev }),
        {},
      ),
    )

    return prev
  }, {}) as D extends unknown ? { [H in M]: Y[H] } : D

export const splitObject = <T extends Dict, K extends keyof T>(
  obj: T,
  keys: K[],
) => {
  const picked: Dict = {}
  const omitted: Dict = {}

  for (const [key, value] of Object.entries(obj)) {
    if (keys.includes(key as T[K])) {
      picked[key] = value
    } else {
      omitted[key] = value
    }
  }

  return [picked, omitted] as [{ [P in K]: T[P] }, Omit<T, K>]
}

export const filterObject = <T extends Dict, K extends Dict>(
  obj: T,
  func: (key: keyof T, value: T[keyof T], obj: T) => boolean,
): K => {
  const result: Dict = {}

  Object.entries(obj).forEach(([key, value]) => {
    const shouldPass = func(key, value, obj)

    if (shouldPass) result[key] = value
  })

  return result as K
}

export const filterUndefined = <T extends Dict>(obj: T): T =>
  filterObject(obj, (_, val) => val !== null && val !== undefined)

export const merge = <T extends Dict>(
  target: any,
  source: any,
  mergeArray: boolean = false,
): T => {
  let result = Object.assign({}, target)

  if (isObject(source)) {
    if (isObject(target)) {
      for (const [sourceKey, sourceValue] of Object.entries(source)) {
        const targetValue: any = target[sourceKey]

        if (mergeArray && isArray(sourceValue) && isArray(targetValue)) {
          result[sourceKey] = targetValue.concat(...sourceValue)
        } else if (
          !isFunction(sourceValue) &&
          isObject(sourceValue) &&
          target.hasOwnProperty(sourceKey)
        ) {
          result[sourceKey] = merge(targetValue, sourceValue, mergeArray)
        } else {
          Object.assign(result, { [sourceKey]: sourceValue })
        }
      }
    } else {
      result = source
    }
  }

  return result as T
}

export const flattenObject = <T extends Dict>(
  obj: any,
  maxDepth: number = Infinity,
  omitKeys: string[] = [],
): T => {
  if ((!isObject(obj) && !isArray(obj)) || !maxDepth) return obj

  return Object.entries(obj).reduce((result, [key, value]) => {
    if (
      isObject(value) &&
      !Object.keys(value).some((key) => omitKeys.includes(key))
    ) {
      Object.entries(flattenObject(value, maxDepth - 1, omitKeys)).forEach(
        ([childKey, childValue]) => {
          result[`${key}.${childKey}`] = childValue
        },
      )
    } else {
      result[key] = value
    }

    return result
  }, {} as any) as T
}

export const objectFromEntries = <T extends Dict>(entries: any[][]): T =>
  entries.reduce((result, [key, value]) => {
    result[key] = value

    return result
  }, {} as any) as T

export const keysFormObject = <T extends Dict>(obj: T): (keyof T)[] =>
  Object.keys(obj)

export const replaceObject = <T extends any>(
  objOrArray: T,
  callBack: (value: any) => any,
): T => {
  if (isArray(objOrArray)) {
    return objOrArray.map(callBack) as T
  } else if (isObject(objOrArray)) {
    return Object.entries(objOrArray).reduce((obj, [key, value]) => {
      obj[key] = callBack(value)

      return obj
    }, {} as Dict) as T
  } else {
    return callBack(objOrArray)
  }
}

export const getObject = (
  obj: Dict,
  path: string | number,
  fallback?: any,
  i?: number,
) => {
  const k = typeof path === "string" ? path.split(".") : [path]

  for (i = 0; i < k.length; i += 1) {
    if (!obj) break

    obj = obj[k[i]]
  }

  return obj === undefined ? fallback : obj
}

export const memoizeObject = (func: typeof getObject) => {
  const cache = new WeakMap()

  const memoizedFunc = <T extends unknown = any>(
    obj: Dict,
    path: string | number,
    fallback?: any,
    i?: number,
  ): T => {
    if (typeof obj === "undefined") return func(obj, path, fallback)

    if (!cache.has(obj)) cache.set(obj, new Map())

    const map = cache.get(obj)

    if (map.has(path)) return map.get(path)

    const value = func(obj, path, fallback, i)

    map.set(path, value)

    return value as T
  }

  return memoizedFunc
}

export const getMemoizedObject = memoizeObject(getObject)

export const assignAfter = (target: Record<string, any>, ...sources: any[]) => {
  if (target == null)
    throw new TypeError("Cannot convert undefined or null to object")

  const result: Record<string, unknown> = { ...target }

  for (const nextSource of sources) {
    if (nextSource == null) continue

    for (const nextKey in nextSource) {
      if (!Object.prototype.hasOwnProperty.call(nextSource, nextKey)) continue

      if (nextKey in result) delete result[nextKey]

      result[nextKey] = nextSource[nextKey]
    }
  }

  return result
}
