import { ResponsiveObject, ColorModeArray, StyledTheme, ColorMode, Theme } from '@yamada-ui/core';

/**
 * `useValue` is a custom hook that combines `useBreakpointValue` and `useColorModeValue`.
 *
 * @see Docs https://yamada-ui.com/hooks/use-value
 */
declare const useValue: <T extends unknown>(value: T | ResponsiveObject<T> | ColorModeArray<T>) => T;
declare const getValue: <T extends unknown>(value: T | ResponsiveObject<T> | ColorModeArray<T>) => (theme: StyledTheme, colorMode: ColorMode, breakpoint: Theme["breakpoints"]) => T;

export { getValue, useValue };
